configuration.value:
flags = [
  U: 0b00100000, // URG
  A: 0b00010000, // ACK
  P: 0b00001000, // PSH
  R: 0b00000100, // RST
  S: 0b00000010, // SYN
  F: 0b00000001  // FIN
]

def getFlags(int intFlags) {
  def sb = new StringBuilder()

  flags.each { flag, mask ->
    if ((intFlags & mask) != 0) {
      sb.append(flag)
    } else {
      sb.append('.')
    }
  }
  return sb.toString()
}

for (record in records) {
  try {
    record.value?.flag = getFlags(record.value?.flag)
    // Write a record to the processor output
    output.write(record)
  } catch (e) {
    // Write a record to the error pipeline
    log.error(e.toString(), e)
    error.write(record, e.toString())
  }
}
